import pyautogui, keyboard, time
from PIL import Image
import base64
from io import BytesIO

wheat_b64 = ''
wheat_img = Image.open(BytesIO(base64.b64decode(wheat_b64)))

def find_and_click_wheat():
    # Look for wheat on screen
    wheat_location = pyautogui.locateOnScreen(wheat_img, confidence=0.1)
    if wheat_location:
        center = pyautogui.center(wheat_location)
        pyautogui.click(center.x, center.y)
        time.sleep(0.1)
        # Right-click to replant (assuming seeds in hotbar slot)
        pyautogui.rightClick(center.x, center.y)

print("Press 'q' to stop farming.")
time.sleep(2)

while not keyboard.is_pressed("q"):
    find_and_click_wheat()
    time.sleep(0.2)